from time import time
# 建立一个字典，通过哈希查找，获得是否有差值
# 加入答案的时候利用哈希判断答案是否重复
# 很慢
class Solution:
    def threeSum(self, nums: list[int]) -> list[list[int]]:
        if len(nums) < 3:
            return []

        answer = set()
        counts = {}
        counts[nums[0]] = 1
        counts[nums[1]] = 2 if nums[1] in counts else 1
        for i in range(2,len(nums)):
            for j in range(i):
                de = 0 - nums[i] - nums[j]
                count = counts.get(de,0)
                if count == 1 and de != nums[j] or count >= 2:
                    temp_answer = tuple(sorted([de,nums[i],nums[j]]))
                    if temp_answer not in answer:
                        answer.add(temp_answer)
            counts[nums[i]] = counts.get(nums[i], 0) + 1
        answer = [list(i) for i in answer]
        answer.sort(key = lambda x:(x[0],x[1],x[2]))
        return answer

# 双指针法
class Solution:
    def threeSum(self, nums: list[int]) -> list[list[int]]:
        n = len(nums)
        nums.sort()
        ans = list()
        
        # 枚举 a
        for first in range(n):
            # 需要和上一次枚举的数不相同
            if first > 0 and nums[first] == nums[first - 1]:
                continue
            # c 对应的指针初始指向数组的最右端
            third = n - 1
            target = -nums[first]
            # 枚举 b
            for second in range(first + 1, n):
                # 需要和上一次枚举的数不相同
                if second > first + 1 and nums[second] == nums[second - 1]:
                    continue
                # 需要保证 b 的指针在 c 的指针的左侧
                while second < third and nums[second] + nums[third] > target:
                    third -= 1
                # 如果指针重合，随着 b 后续的增加
                # 就不会有满足 a+b+c=0 并且 b<c 的 c 了，可以退出循环
                if second == third:
                    break
                if nums[second] + nums[third] == target:
                    ans.append([nums[first], nums[second], nums[third]])
        
        return ans

